import { useState } from "react";
import "./App.css";

function App() {
  const [name, setName] = useState("");
  const [age, setAge] = useState(0);

  const displayInfo = () => {
    console.log(name + " " + age);
  };
  return (
    <div className="App">
      <div className="info">
        <label>Name: </label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <label>Age: </label>
        <input
          type="number"
          onChange={(event) => {
            setAge(event.target.value);
          }}
        />
        <button onClick={displayInfo}>Add Employee </button>
      </div>
    </div>
  );
}

export default App;
